import React, {Component} from 'react'
import './Style/Ticket.css'
import CitilinkIcon from "./Image/citilink.png";
class Ticket extends Component {

    constructor(props) {
        super(props)
        this.state = {
            mobile: true,
            data:[]
        }
    }

    componentDidMount() {
        window.addEventListener("resize", this.resize.bind(this))
        this.resize()
        fetch('https://api.spacexdata.com/v3/launches')
            .then(res => {return res.json()})
            .then(dataRes => {
                var data_map = dataRes.result.map((data) => {
                    return data
                })
                this.setState({
                    data: data_map
                })
                console.log(this.state.data)
            })
    }

    resize = () => {
        if (window.innerWidth <= 720) {
            this.setState({mobile: true})
        } else {
            this.setState({mobile: false})
        }
    }
    render() {
        return(
            <div className="ticket-plane">
                <nav className='navbar'>
                    <a className="navbar-brand" href="/">
                        <a href="/"><i className="fas fa-arrow-left"/></a>
                        <span>Jakarta-Bali</span><br/>
                        <p>Jumat, 30 November 2018(1 Dewasa)</p>
                    </a>
                </nav>
                <div className="container-fluid">
                    <div className="card">
                        <div className="shadow card-body">

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Ticket
